import { Fragment } from "react/cjs/react.production.min";
import Method from "../ContactME/methods";
import React, { useState } from "react";
import userInput from "../../Hooks/user-input";
import "./contact_me.css";

const CheckTextIsValid = (value) => {
  return value.trim() !== "";
};

const CheckEmailIsValid = (value) => {
  return value.includes("@");
};

const Contact_Me = () => {

const {
    value: enteredValue,
    hasError:nameInputHasError,
    ValueChangeHandler:nameChangedHandler,
    InputBlurHandler:nameBlurHandler,
    isValid:valueIsValid
  }=userInput(CheckTextIsValid);

  const [enteredEmail, SetEnteredEmail] = useState("");
  const [EmailIsValid, SetEnteredEmailIsValid] = useState(false);
  const [emailIstouched, SetEmailIsTouched] = useState(false)
  ;
  const [enteredMsg, SetEnteredMsg] = useState("");
  const [MsgIsValid, SetEnteredMsgIsValid] = useState(false);
  const [MsgIstouched, SetMsgIsTouched] = useState(false);

  const NameValidity = !nameIsValid && nameIstouched;
  const EmailValidity = !EmailIsValid && emailIstouched;
  const MsgValidity = !MsgIsValid && MsgIstouched;

  const NameChangeHandler = (event) => {
    SetEnteredName(event.target.value);
    SetEnteredNameIsValid(CheckTextIsValid(event.target.value));
    console.log(event.target.value);
  };

  const EmailChangeHandler = (event) => {
    SetEnteredEmail(event.target.value);
    SetEnteredEmailIsValid(CheckEmailIsValid(event.target.value));
    console.log(event.target.value);
  };

  const MsgChangeHandler = (event) => {
    SetEnteredMsg(event.target.value);
    SetEnteredMsgIsValid(CheckTextIsValid(event.target.value));
    console.log(event.target.value);
  };

  const FormSubmitHandler = (event) => {
    event.preventDefault();
    SetEnteredName("");
    SetEnteredMsg("");
    SetEnteredEmail("");
    SetEmailIsTouched(true);
    SetNameIsTouched(true);
    SetMsgIsTouched(true);
  };

  const NameInputBlurHandler = () => {
    SetNameIsTouched(true);
  };

  const EmailInputBlurHandler = () => {
    SetEmailIsTouched(true);
  };

  const MsgInputBlurHandler = () => {
    SetMsgIsTouched(true);
  };

  let FormIsValid = false;

  if (nameIsValid && EmailIsValid && MsgIsValid) FormIsValid = true;

  return (
    <Fragment>
      <div className="contact_me_container">
        <h1 className="contact_me_heading">Contact_Me_</h1>
        <div className="data_container">
          <div className="contact_methods">
            <Method></Method>
          </div>
          <form
            className="contact_details"
            onSubmit={FormSubmitHandler}
          >
            <div className="name_email">
              <input
                type="text"
                required
                className={`name_ip ${!NameValidity ? "noError" : "error"}`}
                placeholder="NAME"
                onChange={NameChangeHandler}
                value={enteredName}
                onBlur={NameInputBlurHandler}
              ></input>
              <input
                type="email"
                required
                className={`email_ip ${!EmailValidity ? "noError" : "error"}`}
                placeholder="EMAIL"
                onChange={EmailChangeHandler}
                value={enteredEmail}
                onBlur={EmailInputBlurHandler}
              ></input>
            </div>
            <div style={{ width: "90%" }}>
              <input
                type="text"
                required
                className={`message ${!MsgValidity ? "noError" : "error"}`}
                placeholder="MESSAGE"
                onChange={MsgChangeHandler}
                value={enteredMsg}
                onBlur={MsgInputBlurHandler}
              ></input>
            </div>
            <button
              type="submit"
              disabled={!FormIsValid}
              className="submit"
            >
              Send
            </button>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

export default Contact_Me;
