import React, { useState } from "react";
import { Fragment } from "react/cjs/react.production.min";
import './options.css';

const Options=()=>{
   const [selectedOption,OptionsChangeHandler]=useState(1);

   const UnderlineHandler_1=()=>{
       OptionsChangeHandler(1);
   }
   const UnderlineHandler_2=()=>{
       OptionsChangeHandler(2);
   }
   const UnderlineHandler_3=()=>{
       OptionsChangeHandler(3);
   }
   const UnderlineHandler_4=()=>{
       OptionsChangeHandler();
   }
    return(
        <Fragment>
            <div className="options_container">
            <div className="option">
                <div className='option_list' onClick={UnderlineHandler_1}>Home
                {selectedOption==1 && <div className="underline"></div> }
                </div>
                <div className='option_list' onClick={UnderlineHandler_2}>Resources
                {selectedOption==2 && <div className="underline"></div> }
                </div>
                <div className='option_list' onClick={UnderlineHandler_3}>Skills
                {selectedOption==3 && <div className="underline"></div> }
                </div>
                <div className='option_list' onClick={UnderlineHandler_4}>About Me
                {selectedOption==4 && <div className="underline"></div> }
                </div>
                </div>
    
            </div>
        </Fragment>
    );
};

export default Options;
