import React, { useState } from "react";
import { Fragment } from "react/cjs/react.production.min";
import './navigation.css';

const Navigation=(props)=>{
    // const [selected,OptionsChangeHandler]=useState(1);
    const [slider,SliderHandler]=useState(false);
   
    const SLiderChangeHandler=()=>{
        SliderHandler(!slider);
    };

    return(

        <Fragment>
        <div className={`navigation_container ${slider || props.scrollDet>99?"shrink":""}`}>
            <div className="open_close_bt" onClick={SLiderChangeHandler}>
            <span className="icon_slider"><ion-icon name={`chevron-${slider || props.scrollDet>99?"back":"forward"}-outline`}></ion-icon></span>
            </div>
            <div className="menu_options">

            </div>
        </div>
        </Fragment>
    );
}

export default Navigation;
