import { Fragment } from 'react';
import React from 'react';
import './Header.css';
import Navigation from '../Navigation/navigation';
import Logo from '../logo/logo';

const Header=(props)=>{
    
    var toggle="";

    const shrink=(selected)=>{

        if(selected)
        toggle="heightDec";
    };

    return(
            <Fragment>
                <header className={`Header ${toggle}`}>    
                <Logo scrollDet={props.scrollDet}></Logo>
                <Navigation onShift={Shrink} scrollDet={props.scrollDet}></Navigation>
                </header>
            </Fragment>
    );
};

export default Header;
