import React, { useState } from "react";
import { Fragment } from "react/cjs/react.production.min";
import './navigation.css';

const Navigation=()=>{
    const [selected,OptionsChangeHandler]=useState(1);
    var pos;
    // const [style,setStyle]=useState({transform:`translateX(${pos}px)`});
    const slide={
        transform:''
    }
    const UnderlineHandler_1=()=>{
        OptionsChangeHandler(1);
        pos=50*(selected-1);   
        slide = { 
            transform: `translateX(${pos}px)` 
        };
    
        console.log(pos);

    }
    const UnderlineHandler_2=()=>{
        OptionsChangeHandler(2);
        pos=50*(selected-1);   
         slide = { 
            transform: `translateX(${pos}px)` 
        };
    
        console.log(pos);

    }
    const UnderlineHandler_3=()=>{
        OptionsChangeHandler(3);
        pos=50*(selected-1);   
        slide = { 
            transform: `translateX(${pos}px)` 
        };
    
        console.log(pos);

    }
    const UnderlineHandler_4=()=>{
        OptionsChangeHandler(4);
        pos=50*(selected-1);  
        slide = { 
            transform: `translateX(${pos}px)` 
        };
     
        console.log(pos);

    }
    // const slide_indicator=()=>{
    //     pos=50*(selected-1);   
    //     console.log(pos);
    //     setStyle(pos);
    // }
    
    return(

        <Fragment>
        <div className="navigation">
            <ul>
                <li className={`list ${selected===1?"active":""} ${selected===1?`indicator_${selected}`:""}`} onClick={UnderlineHandler_1}>
                    <a href="#">
                        <span className="icon"><ion-icon name="home-outline" ></ion-icon></span>
                        <span className="text">Home</span>
                    </a>
                </li>
                <li className={`list ${selected===2?"active":""} ${selected===2?`indicator_${selected}`:""}`} onClick={UnderlineHandler_2}>
                    <a href="#">
                        <span className="icon"><ion-icon name="book-outline"></ion-icon></span>
                        <span className="text">Projects</span>
                    </a>
                </li>
                <li className={`list ${selected===3?"active":""} ${selected===3?`indicator_${selected}`:""}`} onClick={UnderlineHandler_3}>
                    <a href="#">
                        <span className="icon"><ion-icon name="hand-left-outline"></ion-icon></span>
                        <span className="text">Skills</span>
                    </a>
                </li>
                <li className={`list ${selected===4?"active":""} ${selected===4?`indicator_${selected}`:""}`} onClick={UnderlineHandler_4}>
                    <a href="#">
                        <span className="icon"><ion-icon name="information-circle-outline"></ion-icon></span>
                        <span className="text">About</span>
                    </a>
                </li>
                <div className="indicator"style={slide}></div>
            </ul>
        </div>
        </Fragment>
    );
}

export default Navigation;
