import React, { useState } from "react";
import { Fragment } from "react/cjs/react.production.min";
import './skillCard.css';
import html from '../../Assets/html.png';

const SkillCard=()=>{
const [hoverState,SetHoverState]=useState(false);

const HoverEnter=()=>{
SetHoverState(true);
}
const HoverLeave=()=>{
    SetHoverState(false);
};
    return(
        <Fragment>
         <div className="skill_card" onMouseEnter={HoverEnter} onMouseLeave={HoverLeave} style={{boxShadow:`${hoverState?'0 14px 28px rgba(0, 0, 0, .25), 0 10px 10px rgba(0, 0, 0, .22)':' 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24)'}`}}>
             <div className="skill_card_img" style={{height:`${hoverState?'30%':'90%'}`}}>
                 <img className="skill_img" src={html}  style={{width:`${hoverState?'20%':'60%'}`},{transform:`${hoverState?'scale(3)':'scale(1)'}`}}></img>
             </div>
             <div className="skill_card_details" >
                 <h2 style={{transform:`${hoverState?'translateX(-93%) translateY(10%)':''}`}} className="skill_card_header">
                     HTML
                 </h2>
                 <div className="skill_card_level">
                     <ul>
                         <li>My Level : 90%</li>
                         <li>HTML is the standard markup language for Web pages.</li>
                     </ul>
                 </div>
             </div>
         </div>
        </Fragment>
    );

};

export default SkillCard;
