import React, { Fragment, useState } from "react";
import './Theme.css';

const ToggleButton=()=>{
    const [theme,SetTheme]=useState("moon");
    
    const ThemeChanger=()=>{
        if(theme==="moon")
        SetTheme("sunny");

        else
        SetTheme("moon");
    }
    return(
    <Fragment>
        <span className={`icon_theme ${theme}`} onClick={ThemeChanger}><ion-icon name={theme}></ion-icon></span>
    </Fragment>
    );
}

export default ToggleButton;
