import React, { useState } from "react";
import { Fragment } from "react/cjs/react.production.min";
import './skillCard.css';
import html from '../../Assets/html.png';
import react from '../../Assets/react.png';
import javascript from '../../Assets/javascript.png';
import css from '../../Assets/css3.png';

const SkillCard=(props)=>{
const [hoverState,SetHoverState]=useState(false);
const HoverEnter=()=>{
    SetHoverState(true);
}
const HoverLeave=()=>{
    SetHoverState(false);
};

var Image;
if(props.ImgName=='html')
Image=html;
else if(props.ImgName=='css')
Image=css;
else if(props.ImgName=='javascript')
Image=javascript;
else if(props.ImgName=='react')
Image=react;

var hoverSkill;
if(hoverState){
    setTimeout(()=>{
        hoverSkill=<div className="details"><p className="skill_details">{props.info}<br></br>{`My level : ${props.level}`}</p></div>;
    },500)
}
else
hoverSkill=<img src={Image} alt={props.Name} className="skill_image" style={{width:`${hoverState?'20%':'60%'}`},{transform:`${hoverState?'scale(3)':'scale(1)'}`}}></img>;
    return(
        <Fragment>
            <div className="skill_card_holder" onMouseEnter={HoverEnter} onMouseLeave={HoverLeave} style={{boxShadow:`${hoverState?'0 14px 28px rgba(0, 0, 0, .25), 0 10px 10px rgba(0, 0, 0, .22)':' 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24)'}`}}>
                <div className="Image_details" style={{backgroundColor:`${props.color}`}}>
                    {hoverSkill}
                </div>
                <div className="skill_name" style={{border:`2px solid ${props.color}`}}><h2 className="Heading">{props.Name}</h2></div>
            </div>
         {/* <div className="skill_card" onMouseEnter={HoverEnter} onMouseLeave={HoverLeave} style={{boxShadow:`${hoverState?'0 14px 28px rgba(0, 0, 0, .25), 0 10px 10px rgba(0, 0, 0, .22)':' 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24)'}`},{border:`2px solid ${props.color}`}}>
             <div className="skill_card_img" style={{height:`${hoverState?'30%':'90%'}`},{backgroundColor:`${props.color}`}}>
                 <img className="skill_img" src={Image} alt={props.Name} style={{width:`${hoverState?'20%':'60%'}`},{transform:`${hoverState?'scale(3)':'scale(1)'}`}}></img>
             </div>
             <div className="skill_card_details" >
                 <h2  className="skill_card_header">
                     {props.Name}
                 </h2>
                 
             </div>
         </div> */}
        </Fragment>
    );

};

export default SkillCard;
