import { Fragment } from "react";
import { Link } from "react-router-dom";
import React from "react";
import "./Header.css";
import Navigation from "../Navigation/navigation";
import Logo from "../logo/logo";
import LogoPhone from "../logo/logoPhone";
import { useState } from "react";

const Header = (props) => {
  const [slide, SetSlide] = useState(true);
  const ClickedBt = (status) => {
    SetSlide(status);
  };

  let CurrPath = window.location.pathname;
  return (
    <Fragment>
      <header
        className={`Header ${props.scrollDet > 99 ? "heightDec" : ""} ${
          props.HeaderDown ? "down" : ""
        }`}
      >
        <Logo scrollDet={props.scrollDet}></Logo>
        <LogoPhone slide={slide}></LogoPhone>
        {CurrPath == "/Home" ? (
          <Navigation
            scrollDet={props.scrollDet}
            shift={ClickedBt}
          ></Navigation>
        ) : (
          <Link to="/Home" className="back_link">
            <span className="back_icon">
              <ion-icon name="return-down-back"></ion-icon>
            </span>
          </Link>
        )}
      </header>
    </Fragment>
  );
};

export default Header;
