import React, { useState } from "react";
import { Fragment } from "react/cjs/react.production.min";
import './navigation.css';

const Navigation=(props)=>{
    const [selected,OptionsChangeHandler]=useState(1);
    const [scrollto,SetScrollTo]=useState(0);

    window.scroll({
        top:scrollto,
        left:0,
        behavior:'instant'
    });

    const UnderlineHandler_1=()=>{
        OptionsChangeHandler(1);
        SetScrollTo(0);
    }
    const UnderlineHandler_2=()=>{
        OptionsChangeHandler(2);
    }
    const UnderlineHandler_3=()=>{
        OptionsChangeHandler(3);
       SetScrollTo(700);
    }
    const UnderlineHandler_4=()=>{
        OptionsChangeHandler(4);
        SetScrollTo(1250);
    }

    return(

        <Fragment>
        <div className="navi_handle">
        <div className={`navigation ${props.scrollDet>99?"shrink":""}`}>
            <ul >
                <li className={`list ${selected===1?"active":""}`} onClick={UnderlineHandler_1}>
                    <a href="#">
                        <span className="icon"><ion-icon name="home"></ion-icon></span>
                        <span className="text">Home</span>
                    </a>
                </li>
                <li className={`list ${selected===2?"active":""}`} onClick={UnderlineHandler_2}>
                    <a href="#">
                        <span className="icon"><ion-icon name="book"></ion-icon></span>
                        <span className="text">Projects</span>
                    </a>
                </li>
                <li className={`list ${selected===3?"active":""}`} onClick={UnderlineHandler_3}>
                    <a href="#">
                        <span className="icon"><ion-icon name="hand-left"></ion-icon></span>
                        <span className="text">Skills</span>
                    </a>
                </li>
                <li className={`list ${selected===4?"active":""}`} onClick={UnderlineHandler_4}>
                    <a href="#">
                        <span className="icon"><ion-icon name="information-circle"></ion-icon></span>
                        <span className="text">About</span>
                    </a>
                </li>
                <div className={`indicator indicator_${selected}`}></div>
            </ul>
        </div>
        </div>
        </Fragment>
    );
}

export default Navigation;
