export const SET_TIME = 'SET_TIME';
export const ADD_LOG = 'ADD_LOG';
export const CLEAR_LOGS = 'CLEAR_LOGS';

export const setTime = payload =>
  ({
    payload,
    type: SET_TIME,
  });

export const addLog = payload =>
  ({
    payload,
    type: ADD_LOG,
  });

export const clearLogs = payload =>
  ({
    type: CLEAR_LOGS,
  });
